import React, { useState, useEffect } from 'react';
import { View, StyleSheet } from 'react-native';
import Background from '../components/Background';
import Header from '../components/Header';
import Button from '../components/Button';
import TextInput from '../components/TextInput';
import { nameValidator } from '../helpers/nameValidator';
import { zipcodeValidator } from '../helpers/zipcodeValidator';

export default function EmailSignUp({ navigation }) {
  const [name, setName] = useState({ value: '', error: '' });
  const [zipcode, setZipcode] = useState({ value: '', error: '' });
  const [isChecked, setIsChecked] = useState(false);
  const [isAllFieldsFilled, setIsAllFieldsFilled] = useState(false);

  useEffect(() => {
    // Check if all required fields are filled
    setIsAllFieldsFilled(
      name.value.trim() !== '' &&
      zipcode.value.trim() !== ''
    );
  }, [name.value, zipcode.value]);

  const onSignUpPressed = () => {
    const nameError = nameValidator(name.value);
    const zipcodeError = zipcodeValidator(zipcode.value);

    if (nameError || zipcodeError) {
      setName({ ...name, error: nameError });
      setZipcode({ ...zipcode, error: zipcodeError });
      return;
    }

    navigation.reset({
      index: 0,
      routes: [
        {
          name: 'Dashboard',
          params: {
            userName: name.value,
          },
        },
      ],
    });
  };

  return (
    <Background>
      <Header>Tell us more about yourself</Header>
      <TextInput
        title="First name"
        label="Enter your first name"
        returnKeyType="next"
        value={name.value}
        onChangeText={(text) => setName({ value: text, error: '' })}
        error={!!name.error}
        errorText={name.error}
      />
      <TextInput
        title="Last name"
        label="Enter your last name"
        returnKeyType="next"
        value={name.value}
        onChangeText={(text) => setName({ value: text, error: '' })}
        error={!!name.error}
        errorText={name.error}
      />
      <TextInput
        title="Your zipcode"
        label="Enter your zipcode"
        returnKeyType="done"
        keyboardType="numeric"
        maxLength={5}
        value={zipcode.value}
        onChangeText={(text) => setZipcode({ value: text, error: '' })}
        error={!!zipcode.error}
        errorText={zipcode.error}
      />
      <View style={{ flexDirection: 'row', alignItems: 'center' }}>
        <Checkbox
          status={isChecked ? 'checked' : 'unchecked'}
          onPress={() => setIsChecked(!isChecked)}
        />
        <Text>Remember Me</Text>
      </View>
      <Button
        color={isAllFieldsFilled ? 'black' : 'gray'} // Change the color based on whether all required fields are filled
        mode="contained"
        onPress={onSignUpPressed}
        style={{ marginTop: 24 }}
      >
        Sign Up
      </Button>
    </Background>
  );
}


const styles = StyleSheet.create({
  row: {
    flexDirection: 'row',
    marginTop: 4,
  },
  link: {
    fontWeight: 'bold',
    color: theme.colors.primary,
  },
})
