        useNativeDriver: false,
      }),
      Animated.sequence([
        Animated.timing(fadeAnim, {
          toValue: 0,
          duration: 100,
          useNativeDriver: false,
        }),
        Animated.timing(fadeAnim, {
          toValue: 1,
          duration: 1900,
          useNativeDriver: false,
        }),
      ]),
    ]).start();
  };

  return (
    <Background>
      <ProgressBar progress={progress} />
      <View style={styles.answerCardContainer}>
        <AnswerCard
          question={allQuestions[currentQuestionIndex]?.question}
          options={allQuestions[currentQuestionIndex]?.options}
          correct_option={allQuestions[currentQuestionIndex]?.correct_option}
        />
      </View>
      <TouchableOpacity
        style={[
          styles.btnNext,
          {
            backgroundColor: "#ffffff",
          },
        ]}
        onPress={handleNext}
      >
        <Text style={styles.btnNextText}>NEXT</Text>
      </TouchableOpacity>
    </Background>
  );
};

const styles = StyleSheet.create({
  answerCardContainer: {
    marginBottom: 20, // Adjust the margin here
  },
  btnNext: {
    borderRadius: 10,
    paddingVertical: 13,
    paddingHorizontal: 20,
    backgroundColor: "#ffffff",
    position: "absolute",
    bottom: 20,
    right: 20,
  },
  btnNextText: {
    color: "#333",
    fontSize: 17,
    letterSpacing: 1.1,
  },
});

export default QuizScreen;
