import React, { useState } from "react";
import { Text, View, StyleSheet, TouchableOpacity } from "react-native";
import { useNavigation } from "@react-navigation/native"; // Import useNavigation hook
import data from "../helpers/data";
import Button from '../components/Button';
import ProgressBar from "./ProgressBar";

const Quiz = () => {
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [isCorrect, setIsCorrect] = useState(null);
    const [selectedOption, setSelectedOption] = useState(null);
    const navigation = useNavigation(); // Initialize navigation

    const handleNext = () => {
        if (currentQuestionIndex === data.length - 1) {
            navigation.navigate("QuizEndingScreen");
        } else {
            setCurrentQuestionIndex(currentQuestionIndex + 1);
            setSelectedOption(null);
            setIsCorrect(null);
        }
    };

    const handlePressedOption = (pressedOption) => {
        setSelectedOption(pressedOption);
        const isAnswerCorrect = data[currentQuestionIndex].correct_option === pressedOption;
        setIsCorrect(isAnswerCorrect);
    };

    return (
        <View style={styles.container}>
            <Text style={styles.question}>{data[currentQuestionIndex]?.question}</Text> {/* Added null check */}
            <TouchableOpacity onPress={() => handlePressedOption(data[currentQuestionIndex]?.options[0])} disabled={selectedOption !== null}>
                <View style={[styles.AnswerBox, isCorrect && selectedOption === data[currentQuestionIndex]?.options[0] ? styles.correctBox : null, !isCorrect && selectedOption === data[currentQuestionIndex]?.options[0] ? styles.wrongBox : null]}>
                    <Text style={styles.answerText}>{data[currentQuestionIndex]?.options[0]}</Text> {/* Added null check */}
                </View>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => handlePressedOption(data[currentQuestionIndex]?.options[1])} disabled={selectedOption !== null}>
                <View style={[styles.AnswerBox, styles.secondAnswerBox, isCorrect && selectedOption === data[currentQuestionIndex]?.options[1] ? styles.correctBox : null, !isCorrect && selectedOption === data[currentQuestionIndex]?.options[1] ? styles.wrongBox : null]}>
                    <Text style={styles.answerText}>{data[currentQuestionIndex]?.options[1]}</Text> {/* Added null check */}
                </View>
            </TouchableOpacity>
            <Button
                color="black"
                mode="contained"
                onPress={handleNext}
            >
                Continue
            </Button>
        </View>
    );
};

export default Quiz;

const styles = StyleSheet.create({
    AnswerBox: {
        width: "100%", // Changed to percentage-based width
        height: 214,
        borderWidth: 3,
        borderRadius: 6,
        borderColor: 'rgba(0, 0, 0, 0.05)',
        backgroundColor: 'rgba(0, 0, 0, 0.05)', 
        alignItems: 'center',
        justifyContent: 'center',
        marginBottom: 10, 
    },
    secondAnswerBox: {
        marginBottom: 0, 
    },
    answerText: {
        textAlign: 'center',
        fontSize: 24,
    },
    question: {
        marginBottom: 10,
        fontSize: 18, 
    },
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
        paddingHorizontal: 20, // Added padding
    },
    correctBox: {
        borderColor: 'green', // Change border color to green for correct answer
    },
    wrongBox: {
        borderColor: 'red', // Change border color to red for wrong answer
    },
});
