import React from 'react';
import { Avatar, Subheading } from 'react-native-paper';
import {View, StyleSheet} from 'react-native';


export function UserPic() {
  return (
   <View style={styles.userContainer}>
   <Avatar.Image
     size={44}
     source={require('../assets/avatarIMG.jpg')}
     style={styles.avatar}
   />
   <View style={styles.textContainer}>
     <Subheading style={styles.userName}>Your Name</Subheading>
     <Subheading style={styles.subtitle}>Eco Novice</Subheading>
   </View>
 </View>

  );
}
const styles = StyleSheet.create({
  userContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  avatar: {
    marginRight: 12, 
  },
  textContainer: {
    flexDirection: 'column',
  },
  userName: {
    fontFamily: 'Poppins',
    fontSize: 16,
    fontWeight: '500',
    lineHeight: 24,
    letterSpacing: 0,
    textAlign: 'left',
  },
  subtitle: {
    fontFamily: 'Poppins',
    fontSize: 12,
    lineHeight: 18,
    letterSpacing: 0,
    textAlign: 'left',
    color: 'gray',
  },
});
