import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Animated } from 'react-native'; // Import Animated
import Questions from '../helpers/Questions';
import data from '../helpers/QuizData';

const AnswerCard = ({ question, options, correct_option }) => {
  const [showOptions, setShowOptions] = useState(true);
  const [selectedOption, setSelectedOption] = useState(null);

  const checkAnswer = () => {
    if (selectedOption === correct_option) {
      return <Text style={styles.correctAnswer}>Correct!</Text>;
    } else {
      return <Text style={styles.wrongAnswer}>Wrong! Correct answer is {correct_option}</Text>;
    }
  };

  const cardMove = () => {
    const position = new Animated.ValueXY({ x: 0, y: 0 });
    Animated.spring(position, {
      toValue: { x: 100, y: 100 }, // Adjust the destination coordinates as needed
      useNativeDriver: false, // Required for non-native animations
    }).start();
  };

  return (
    <>
      <Text style={styles.question}>{question}</Text>
      <Animated.View style={[styles.card, { transform: [{ translateX: 100 }, { translateY: 100 }] }]}>
        <TouchableOpacity onPress={() => { cardMove(); setShowOptions(!showOptions); }} style={styles.card}>
          {showOptions && (
            <View style={styles.options}>
              {options.map((option, index) => (
                <TouchableOpacity
                  key={index}
                  style={styles.option}
                  onPress={() => setSelectedOption(option)}
                >
                  <Text>{option}</Text>
                </TouchableOpacity>
              ))}
              {selectedOption && checkAnswer()}
            </View>
          )}
        </TouchableOpacity>
      </Animated.View>
    </>
  );
};

const styles = StyleSheet.create({
  card: {
    backgroundColor: '#fff',
    borderRadius: 10,
    padding: 20,
    marginBottom: 20,
  },
  question: {
    fontSize: 18,
    fontWeight: 'normal',
    marginBottom: 10,
  },
  options: {
    marginTop: 10,
  },
  option: {
    paddingVertical: 30,
    paddingHorizontal: 20,
    backgroundColor: '#f4f4f4',
    borderRadius: 5,
    marginBottom: 10,
    borderStyle: "solid",
    borderWidth: 1,
    borderColor: '#808080',
  },
  correctAnswer: {
    color: 'green',
    marginTop: 10,
  },
  wrongAnswer: {
    color: 'red',
    marginTop: 10,
  },
});

export default AnswerCard;
