import React from 'react';
import { View, Text, StyleSheet, Animated } from 'react-native';
import Button from './Button';
import * as Progress from 'react-native-progress';

const IncorrectQuiz = ({ incorrectQuestions, handleNext, currentQuestionIndex, fadeAnim, selectedOption }) => {
    if (!incorrectQuestions || incorrectQuestions.length === 0 || currentQuestionIndex < 0 || currentQuestionIndex >= incorrectQuestions.length) {
        return null; // Return null if incorrectQuestions is empty or currentQuestionIndex is out of bounds
    }

    const progress = (currentQuestionIndex + 1) / incorrectQuestions.length;
    const currentQuestion = incorrectQuestions[currentQuestionIndex];

    return (
        <View style={styles.container}>
            <View style={styles.progressBarContainer}>
                <Progress.Bar progress={progress} width={350} color='black' unfilledColor='rgba(217, 217, 217, 1)' borderWidth={0} height={8} />
            </View>
            <Animated.View
                style={[{ opacity: selectedOption !== null ? fadeAnim : 1 },]}
            >
                <Text style={styles.question}>{currentQuestion.question}</Text>
            </Animated.View>  
            {incorrectQuestions.map((question, index) => (
                <View key={index} style={styles.questionContainer}>
                    <Text style={styles.question}>{question.question}</Text>
                    {question.options.map((option, optionIndex) => (
                        <Text key={optionIndex} style={styles.option}>{option}</Text>
                    ))}
                </View>
            ))}
            <Button
                color="black"
                mode="contained"
                onPress={handleNext}
            >
                Continue
            </Button>
        </View>
    );
};

export default IncorrectQuiz;

const styles = StyleSheet.create({
    question: {
        marginBottom: 10,
        fontSize: 18,
    },
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    progressBarContainer: {
        width: '100%',
        height: 20,
        marginBottom: 30,
    },
});
