import React, { useState, useRef  } from "react";
import { Text, View, StyleSheet, TouchableOpacity, Animated  } from "react-native";
import data from "../helpers/data";
import Button from '../components/Button';
import * as Progress from 'react-native-progress';


const Quiz = ({navigation}) => {
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [isCorrect, setIsCorrect] = useState(null);
    const [selectedOption, setSelectedOption] = useState(null);
    const [quizProgress, setQuizProgress] = useState(data.length);
    const translateY = useRef(new Animated.Value(0)).current;
    const fadeAnim = useRef(new Animated.Value(1)).current;


    const progress = (currentQuestionIndex + 1) / quizProgress

    const handleNext = () => {
        if(currentQuestionIndex === data.length -1 ){
        navigation.navigate("QuizEndingScreen");
        }
            else{
        setCurrentQuestionIndex(currentQuestionIndex + 1);
        setSelectedOption(null);
        setIsCorrect(null);
        fadeAnim.setValue(1); 
        translateY.setValue(0); 
    }
};
const fadeOut = () => {
    Animated.parallel([
      Animated.timing(fadeAnim, {
        toValue: 0,
        duration: 200,
        useNativeDriver: true,
      })
    ]).start();
  };
const moveCardUp=() => {
    Animated.parallel([
      Animated.timing(translateY, {
        toValue: -300, 
        duration: 600,
        useNativeDriver: true,
      }),
    ]).start();
  }





const handlePressedOption = (pressedOption) => {
    console.log("Selected Option before update:", selectedOption);
    const isAnswerCorrect = data[currentQuestionIndex].correct_option === pressedOption;
    setIsCorrect(isAnswerCorrect);

    setSelectedOption(pressedOption); // Update selectedOption

    console.log("Selected Option after update:", selectedOption);

    if (pressedOption !== selectedOption) {
        fadeOut();
    }
    moveCardUp(translateY); 
};




    return(
        <View style={styles.container}>
            <View style={styles.progressBarContainer}>
                <Progress.Bar progress={progress} width={350} color= 'black' unfilledColor='rgba(217, 217, 217, 1)' borderWidth={0} height={8}  />
            </View>
            <Animated.View style={{ opacity: fadeAnim, transform: [{ translateY }] }}> 
                <Text style={styles.question}>{data[currentQuestionIndex].question}</Text>
                <TouchableOpacity onPress={() => { 
    console.log("Pressed Option 1:", data[currentQuestionIndex].options[0]); // Add this line
    handlePressedOption(data[currentQuestionIndex].options[0]); 
}} disabled={selectedOption}></TouchableOpacity>
                    <View style={[styles.AnswerBox, isCorrect && selectedOption === data[currentQuestionIndex].options[0] ? styles.correctBox : null, !isCorrect && selectedOption === data[currentQuestionIndex].options[0] ? styles.wrongBox : null]}>
                        <Text style={styles.answerText}>{data[currentQuestionIndex].options[0]}</Text>
                    </View>

                <TouchableOpacity onPress={() => { 
    console.log("Pressed Option 2:", data[currentQuestionIndex].options[1]); // Add this line
    handlePressedOption(data[currentQuestionIndex].options[1]); 
}} disabled={selectedOption}></TouchableOpacity>
                    <View style={[styles.AnswerBox, styles.secondAnswerBox, isCorrect && selectedOption === data[currentQuestionIndex].options[1] ? styles.correctBox : null, !isCorrect && selectedOption === data[currentQuestionIndex].options[1] ? styles.wrongBox : null]}>
                        <Text style={styles.answerText}>{data[currentQuestionIndex].options[1]}</Text>
                    </View>
            </Animated.View>
            <Button
                color="black"
                mode="contained"
                onPress={handleNext}
            >
                Continue
            </Button>
        </View>
    );
}

export default Quiz;

const styles = StyleSheet.create({
    AnswerBox: {
        width: 361,
        height: 214,
        borderWidth: 3,
        borderRadius: 6,
        borderColor: 'rgba(0, 0, 0, 0.05)',
        backgroundColor: 'rgba(0, 0, 0, 0.05)', 
        alignItems: 'center',
        justifyContent: 'center',
        marginBottom: 10, 
    },
    secondAnswerBox: {
        marginBottom: 0, 
    },
    answerText: {
        textAlign: 'center',
        fontSize: 24,
    },
    question: {
        marginBottom: 10,
        fontSize: 18, 
    },
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    correctBox: {
        borderColor: 'rgba(19, 212, 2, 1)', 
    },
    wrongBox: {
        borderColor: 'rgba(241, 58, 0, 1)', 
    },
    progressBarContainer: {
        width: '100%',
        height: 20,
        marginBottom: 30,
    },
});
