import React, { useState, useRef } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Animated } from 'react-native';

const AnswerCard = ({ question, options, correct_option }) => {
  const [selectedOption, setSelectedOption] = useState(null);
  const [text, setText] = useState(null); // Initialize text state to null
  const translateY = useRef(new Animated.Value(0)).current;
  const opacity = useRef(new Animated.Value(1)).current;

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
    animateSelectedOption();
    setText(option); // Update text content when an option is selected
  };

  const animateSelectedOption = () => {
    Animated.parallel([
      Animated.timing(translateY, {
        toValue: -100,
        duration: 600,
        useNativeDriver: true,
      }),
      Animated.timing(opacity, {
        toValue: 0,
        duration: 600,
        useNativeDriver: true,
      }),
    ]).start();
  };

  return (
    <View style={styles.container}>
      <Animated.Text style={[styles.question, { opacity: opacity }]}>{question}</Animated.Text>
      {options.map((option, index) => (
        <Animated.View
          key={index}
          style={[
            styles.optionContainer,
            {
              opacity: selectedOption === null || selectedOption === option ? 1 : 0,
              transform: [{ translateY: selectedOption === option ? translateY : 0 }],
            },
          ]}
        >
          <TouchableOpacity
            onPress={() => handleOptionSelect(option)}
            style={styles.option}
            disabled={selectedOption !== null}
          >
            <Text style={styles.optionText}>{option}</Text>
          </TouchableOpacity>
        </Animated.View>
      ))}
      {selectedOption && (
        <View style={styles.textContainer}>
          <Text style={styles.resultText}>
            {selectedOption === correct_option ? 'Correct!' : `Wrong! Correct answer is ${correct_option}`}
          </Text>
          <Text style={styles.selectedOptionText}>{text}</Text>
        </View>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 20,
  },
  question: {
    fontSize: 18,
    fontWeight: 'normal',
    marginBottom: 10,
    textAlign: 'center',
  },
  optionContainer: {
    alignItems: 'center',
    marginBottom: 10,
  },
  option: {
    backgroundColor: '#f4f4f4',
    borderRadius: 6,
    height: 150,
    width: 200,
    justifyContent: 'center',
    borderColor: 'rgba(0, 0, 0, 0.1)',
    borderWidth: 1,
  },
  optionText: {
    fontSize: 18,
    textAlign: 'center',
  },
  textContainer: {
    alignItems: 'center',
  },
  resultText: {
    fontSize: 18,
    marginTop: 10,
    textAlign: 'center',
  },
  selectedOptionText: {
    fontSize: 18,
    textAlign: 'center',
    fontWeight: 'bold',
  },
});

export default AnswerCard;
