import React, { useEffect, useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Animated } from 'react-native'; // Import Animated
import Questions from '../helpers/Questions';
import data from '../helpers/QuizData';
import { useRef } from 'react';

const AnswerCard = ({ question, options, correct_option }) => {
  const [showOptions, setShowOptions] = useState(true);
  const [selectedOption, setSelectedOption] = useState(null);

  const checkAnswer = () => {
    if (selectedOption === correct_option) {
      return <Text style={styles.correctAnswer}>Correct!</Text>;
    } else {
      return <Text style={styles.wrongAnswer}>Wrong! Correct answer is {correct_option}</Text>;
    }
  };

  const animatedValue = useRef(new Animated.Value(0)).current;

  useEffect(() => {
    Animated.timing(animatedValue, {toValue: 4000,duration: 4000, useNativeDriver: false}).start();
}, []);
  return (

    <View style={styles.container} >
      <Text style={styles.question}>{question}</Text>
        <TouchableOpacity onPress={() => {  setShowOptions(!showOptions); }}>
          {showOptions && (
            <View >
              {options.map((option, index) => (
                <TouchableOpacity
                  key={index}
                  style={[styles.option , index > 0 && { marginTop: 24 }]} 
                  onPress={() => setSelectedOption(option)}
                >
                  <Text style = {styles.optionText}>{option}</Text>
                </TouchableOpacity>
              ))}
              {selectedOption && checkAnswer()}
            </View>
          )}
        </TouchableOpacity>

    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    marginBottom: 20,
  },

  question: {
    fontSize: 18,
    fontWeight: 'normal',
    marginBottom: 10,

  },

  option: {

    backgroundColor: '#f4f4f4',
    borderRadius: 6,
    marginBottom: 10,
    borderStyle: "solid",
    borderWidth: 1,
    borderColor: '#808080',
    height: 214, // Fixed height
    width: '100%',
    justifyContent: 'center',
  },
  optionText: {
    fontSize: 24,
    textAlign: 'center',
  },
  correctAnswer: {
    color: 'green',
    marginTop: 10,
  },
  wrongAnswer: {
    color: 'red',
    marginTop: 10,
  },
});

export default AnswerCard;
