import React, { useState, useRef } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Animated } from 'react-native';

const AnswerCard = ({ question, options, correct_option }) => {
  const [showOptions, setShowOptions] = useState(true);
  const [selectedOption, setSelectedOption] = useState(null);
  const animatedValue = useRef(new Animated.Value(0)).current;

  const checkAnswer = () => {
    if (selectedOption === correct_option) {
      return <Text style={styles.correctAnswer}>Correct!</Text>;
    } else {
      return <Text style={styles.wrongAnswer}>Wrong! Correct answer is {correct_option}</Text>;
    }
  };

  const animateOptions = () => {
    Animated.timing(animatedValue, {
      toValue: -100,
      duration: 600,
      useNativeDriver: true,
    }).start();
  };

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
    animateOptions();
  };

  return (
    <View style={styles.container}>
      <Text style={styles.question}>{question}</Text>
      {showOptions && (
        <View>
          {options.map((option, index) => (
            <Animated.View
              key={index}
              style={[
                styles.optionContainer,
                {
                  transform: [{ translateY: animatedValue }],
                  marginTop: index > 0 ? 24 : 0,
                },
              ]}
            >
              <TouchableOpacity
                onPress={() => handleOptionSelect(option)}
                style={styles.option}
                disabled={selectedOption !== null}
              >
                <Text style={styles.optionText}>{option}</Text>
              </TouchableOpacity>
            </Animated.View>
          ))}
          {selectedOption && checkAnswer()}
        </View>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    marginBottom: 20,
  },
  question: {
    fontSize: 18,
    fontWeight: 'normal',
    marginBottom: 10,
  },
  optionContainer: {
    alignItems: 'center',
  },
  option: {
    backgroundColor: '#f4f4f4',
    borderRadius: 6,
    marginBottom: 10,
    borderWidth: 1,
    borderColor: '#808080',
    height: 50,
    width: '100%',
    justifyContent: 'center',
  },
  optionText: {
    fontSize: 18,
    textAlign: 'center',
  },
  correctAnswer: {
    color: 'green',
    marginTop: 10,
  },
  wrongAnswer: {
    color: 'red',
    marginTop: 10,
  },
});

export default AnswerCard;
