import React, { useState, useEffect, useRef } from "react";
import { View, Text, StyleSheet, Animated, Pressable, Button } from "react-native";
import * as Progress from 'react-native-progress';

const IncorrectQuiz = ({ incorrectQuestions, handleNext, handleOptionPress, selectedOption, isCorrect }) => {
    const translateY = useRef(new Animated.Value(0)).current;
    const fadeAnim = useRef(new Animated.Value(1)).current;
    const [currentIndex, setCurrentIndex] = useState(0);

    useEffect(() => {
        fadeIn();
    }, []);

    const fadeIn = () => {
        Animated.timing(fadeAnim, {
            toValue: 1,
            duration: 500,
            useNativeDriver: true,
        }).start();
    };

    const handleNextQuestion = () => {
        if (currentIndex < incorrectQuestions.length - 1) {
            setCurrentIndex(currentIndex + 1);
            fadeAnim.setValue(1);
        } else {
            handleNext();
        }
    };

    if (incorrectQuestions.length === 0) {
        // No incorrect questions
        return (
            <View style={styles.container}>
                <Text>No incorrect questions found.</Text>
                <Button title="Continue" onPress={handleNext} />
            </View>
        );
    }

    const question = incorrectQuestions[currentIndex];

    return (
        <View style={styles.container}>
            {/* Display incorrect questions */}
            {incorrectQuestions.map((question, index) => (
                <View key={index}>
                    <Text>{question.question}</Text>
                    {/* Display options */}
                    {question.options.map((option, optionIndex) => (
                        <Pressable
                            key={optionIndex}
                            onPress={() => handleOptionPress(option)}
                            style={({ pressed }) => [
                                styles.AnswerBox,
                                // Apply styles based on selected option and correctness
                                selectedOption === option && (isCorrect ? styles.correctBox : styles.wrongBox),
                                { backgroundColor: pressed ? 'rgba(0, 0, 0, 0.2)' : 'transparent' }
                            ]}
                        >
                            <Text style={styles.answerText}>{option}</Text>
                        </Pressable>
                    ))}
                </View>
            ))}
            {/* Button to move to the next question */}
            <Button title="Next Question" onPress={handleNextQuestion} />
        </View>
    );
};

export default IncorrectQuiz;

const styles = StyleSheet.create({
    AnswerBox: {
        width: 361,
        height: 214,
        borderWidth: 3,
        borderRadius: 6,
        borderColor: 'rgba(0, 0, 0, 0.05)',
        backgroundColor: 'rgba(0, 0, 0, 0.05)',
        alignItems: 'center',
        justifyContent: 'center', 
    },

    answerText: {
        fontSize: 18,
    },
    question: {
        fontSize: 18,
    },
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    progressBarContainer: {
        width: '100%',
        height: 20,
        marginBottom: 20,
    },
    button: {
        marginTop: 20,
    },
    questionContainer: {
        alignItems: 'center',
        justifyContent: 'center',
        marginBottom: 20,
    },
});
