import React, { useState, useRef } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Animated } from 'react-native';

const Quiz = ({ navigation }) => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [isCorrect, setIsCorrect] = useState(null);
  const [selectedOption, setSelectedOption] = useState(null);
  const translateY = useRef(new Animated.Value(0)).current;
  const opacity = useRef(new Animated.Value(1)).current;

  const handleNext = () => {
    if (currentQuestionIndex === data.length - 1) {
      console.log("irhohreoihofg")
      navigation.navigate("QuizEndingScreen");
    } else {
      animateQuestionText();
      setCurrentQuestionIndex(currentQuestionIndex + 1);
      setSelectedOption(null);
      setIsCorrect(null);
    }
  };

  const handlePressedOption = (pressedOption) => {
    setSelectedOption(pressedOption);
    const isAnswerCorrect = data[currentQuestionIndex].correct_option === pressedOption;
    setIsCorrect(isAnswerCorrect);
    animateSelectedOption();
  };

  const animateSelectedOption = () => {
    // Reset animation values
    translateY.setValue(0);
    opacity.setValue(1);

    Animated.parallel([
      Animated.timing(translateY, {
        toValue: -100,
        duration: 600,
        useNativeDriver: true,
      }),
      Animated.timing(opacity, {
        toValue: 0,
        duration: 600,
        useNativeDriver: true,
      }),
    ]).start();
  };

  const animateQuestionText = () => {
    Animated.timing(opacity, {
      toValue: 0,
      duration: 600,
      useNativeDriver: true,
    }).start();
  };

  return (
    <View style={styles.container}>
      <Animated.Text style={[styles.question, { opacity: opacity }]}>
        {data[currentQuestionIndex].question}
      </Animated.Text>
      {data[currentQuestionIndex].options.map((option, index) => (
        <Animated.View
          key={index}
          style={[
            styles.optionContainer,
            {
              opacity: selectedOption === null || selectedOption === option ? 1 : 0,
              transform: [{ translateY: selectedOption === option ? translateY : 0 }],
            },
          ]}
        >
          <TouchableOpacity
            onPress={() => handlePressedOption(option)}
            style={styles.option}
            disabled={selectedOption !== null}
          >
            <Text style={styles.optionText}>{option}</Text>
          </TouchableOpacity>
        </Animated.View>
      ))}
      <Button
        color="black"
        mode="contained"
        onPress={handleNext}
      >
        Continue
      </Button>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 20,
  },
  question: {
    fontSize: 18,
    fontWeight: 'normal',
    marginBottom: 10,
    textAlign: 'center',
  },
  optionContainer: {
    alignItems: 'center',
    marginBottom: 10,
  },
  option: {
    backgroundColor: '#f4f4f4',
    borderRadius: 6,
    height: 150,
    width: 200,
    justifyContent: 'center',
    borderColor: 'rgba(0, 0, 0, 0.1)',
    borderWidth: 1,
  },
  optionText: {
    fontSize: 18,
    textAlign: 'center',
  },
});

export default Quiz;
