import React, { useEffect, useRef } from 'react';
import { View, Text, StyleSheet, Animated, Easing } from 'react-native';
import Button from './Button';
import * as Progress from 'react-native-progress';

const IncorrectQuiz = ({ incorrectQuestions, handleNext, totalQuestions }) => {
    const translateY = useRef(new Animated.Value(0)).current;
    const fadeAnim = useRef(new Animated.Value(1)).current;

    useEffect(() => {
        fadeOut();
    }, []);

    const fadeOut = () => {
        Animated.parallel([
            Animated.timing(fadeAnim, {
                toValue: 0,
                duration: 9000,
                useNativeDriver: true,
            }),
            Animated.timing(translateY, {
                toValue: -280,
                duration: 600,
                easing: Easing.out(Easing.quad),
                useNativeDriver: true,
            }),
        ]).start();
    };

    const progress = (incorrectQuestions.length + 1) / totalQuestions; // Progress for incorrect questions

    return (
        <View style={styles.container}>
            <View style={styles.progressBarContainer}>
                <Progress.Bar progress={progress} width={350} color='black' unfilledColor='rgba(217, 217, 217, 1)' borderWidth={0} height={8} />
            </View>

            {incorrectQuestions.map((question, index) => (
                <Animated.View
                    key={index}
                    style={[
                        styles.questionContainer,
                        { opacity: fadeAnim },
                        { transform: [{ translateY: translateY }] },
                    ]}
                >
                    <Text style={styles.question}>{question.question}</Text>
                    {question.options.map((option, optionIndex) => (
                        <Text key={optionIndex} style={styles.AnswerBox}>{option}</Text>
                    ))}
                </Animated.View>
            ))}
            <Button
                color="black"
                mode="contained"
                onPress={handleNext}
                style={styles.button}
            >
                Continue
            </Button>
        </View>
    );
};

export default IncorrectQuiz;

const styles = StyleSheet.create({
    AnswerBox: {
        width: 361,
        height: 214,
        borderWidth: 3,
        borderRadius: 6,
        borderColor: 'rgba(0, 0, 0, 0.05)',
        backgroundColor: 'rgba(0, 0, 0, 0.05)',
        alignItems: 'center',
        justifyContent: 'center', 
        marginBottom: 10,
    },

    question: {
        fontSize: 18,
    },
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    progressBarContainer: {
        width: '100%',
        height: 20,
        marginBottom: 20,
    },
    button: {
        marginTop: 20,
    },
});
