import React, { useRef, useEffect } from "react";
import { View, Text, StyleSheet, Animated } from "react-native";
import Button from '../components/Button';
import * as Progress from 'react-native-progress';

const IncorrectQuiz = ({ incorrectQuestions, handleNext }) => {
    const translateY = useRef(new Animated.Value(0)).current;
    const fadeAnim = useRef(new Animated.Value(1)).current;
    const [currentIndex, setCurrentIndex] = React.useState(0);

    useEffect(() => {
        fadeIn();
    }, []);

    const fadeIn = () => {
        Animated.timing(fadeAnim, {
            toValue: 1,
            duration: 500,
            useNativeDriver: true,
        }).start();
    };

    const handleNextQuestion = () => {
        if (currentIndex < incorrectQuestions.length - 1) {
            setCurrentIndex(currentIndex + 1);
            fadeAnim.setValue(1);
        } else {
            handleNext();
        }
    };

    const question = incorrectQuestions[currentIndex];

    return (
        <View style={styles.container}>
            <View style={styles.progressBarContainer}>
                <Progress.Bar progress={(currentIndex + 1) / incorrectQuestions.length} width={350} color='black' unfilledColor='rgba(217, 217, 217, 1)' borderWidth={0} height={8} />
            </View>

            <Animated.View
                style={[
                    styles.questionContainer,
                    { opacity: fadeAnim },
                    { transform: [{ translateY: translateY }] },
                ]}
            >
                <Text style={styles.question}>{question.question}</Text>
                {question.options.map((option, optionIndex) => (
                    <Text key={optionIndex} style={styles.AnswerBox}>{option}</Text>
                ))}
            </Animated.View>

            <Button
                color="black"
                mode="contained"
                onPress={handleNextQuestion}
                style={styles.button}
            >
                {currentIndex === incorrectQuestions.length - 1 ? 'Continue' : 'Next Question'}
            </Button>
        </View>
    );
};

export default IncorrectQuiz;

const styles = StyleSheet.create({
    AnswerBox: {
        width: 361,
        height: 214,
        borderWidth: 3,
        borderRadius: 6,
        borderColor: 'rgba(0, 0, 0, 0.05)',
        backgroundColor: 'rgba(0, 0, 0, 0.05)',
        alignItems: 'center',
        justifyContent: 'center',
        marginBottom: 10,
    },

    question: {
        fontSize: 18,
    },
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    progressBarContainer: {
        width: '100%',
        height: 20,
        marginBottom: 20,
    },
    button: {
        marginTop: 20,
    },
    questionContainer: {
        alignItems: 'center',
        justifyContent: 'center',
        marginBottom: 20,
    },
});
