import React, { useState, useEffect, useRef } from "react";
import { View, Text, StyleSheet, Animated, Pressable, Button } from "react-native";
import * as Progress from 'react-native-progress';

const IncorrectQuiz = ({ incorrectQuestions, handleNext, selectedOption }) => {
    const translateY = useRef(new Animated.Value(0)).current;
    const fadeAnim = useRef(new Animated.Value(1)).current;
    const [currentIndex, setCurrentIndex] = useState(0);
    const [isCorrect, setIsCorrect] = useState(null); // State for handling correctness


    useEffect(() => {
        fadeIn();
    }, []);

    const fadeIn = () => {
        Animated.timing(fadeAnim, {
            toValue: 1,
            duration: 500,
            useNativeDriver: true,
        }).start();
    };

    const handlePressedOption = (pressedOption) => {
        const currentQuestion = incorrectQuestions[currentIndex];
        const isAnswerCorrect = currentQuestion.correct_option === pressedOption;
        setIsCorrect(isAnswerCorrect);
        setSelectedOption(pressedOption);
        moveCardUp(pressedOption);
        fadeOut();

        if (!isAnswerCorrect) {
            setCustomMessage(currentQuestion.wrong_message);
        } else {
            setCustomMessage(currentQuestion.correct_message);
        }
    };


    const handleNextQuestion = () => {
        if (currentIndex < incorrectQuestions.length - 1) {
            setCurrentIndex(currentIndex + 1);
            fadeAnim.setValue(1);
        } else {
            handleNext();
        }
    };

    if (incorrectQuestions.length === 0) {
        // No incorrect questions
        return (
            <View style={styles.container}>
                <Text>No incorrect questions found.</Text>
                <Button title="Continue" onPress={handleNext} />
            </View>
        );
    }

    const question = incorrectQuestions[currentIndex];

    return (
        <View style={styles.container}>
            <View style={styles.progressBarContainer}>
                <Progress.Bar progress={(currentIndex + 1) / incorrectQuestions.length} width={350} color='black' unfilledColor='rgba(217, 217, 217, 1)' borderWidth={0} height={8} />
            </View>

            <Animated.View
                style={[
                    styles.questionContainer,
                    { opacity: fadeAnim },
                    { transform: [{ translateY: translateY }] },
                ]}
            >
                <Text style={styles.question}>{question.question}</Text>
                {question.options.map((option, optionIndex) => (
                    <Pressable
                        key={optionIndex}
                        onPress={() => handlePressedOption(option)}
                        style={({ pressed }) => [
                            {
                                backgroundColor: pressed ? 'rgba(0, 0, 0, 0.2)' : 'transparent',
                            },
                            styles.AnswerBox,
                            selectedOption === option && (isCorrect ? styles.correctBox : styles.wrongBox),
                        ]}
                        disabled={selectedOption}
                    >
                        <Text style={styles.optionText}>{option}</Text>
                    </Pressable>
                ))}
            </Animated.View>

            <Button
                color="black"
                title={currentIndex === incorrectQuestions.length - 1 ? 'Continue' : 'Next Question'}
                onPress={handleNextQuestion}
                style={styles.button}
            />
        </View>
    );
};

export default IncorrectQuiz;

const styles = StyleSheet.create({
    AnswerBox: {
        width: 361,
        height: 214,
        borderWidth: 3,
        borderRadius: 6,
        borderColor: 'rgba(0, 0, 0, 0.05)',
        backgroundColor: 'rgba(0, 0, 0, 0.05)',
        alignItems: 'center',
        justifyContent: 'center', 
    },
    optionText: {
        fontSize: 18,
    },
    question: {
        fontSize: 18,
    },
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    progressBarContainer: {
        width: '100%',
        height: 20,
        marginBottom: 20,
    },
    button: {
        marginTop: 20,
    },
    questionContainer: {
        alignItems: 'center',
        justifyContent: 'center',
        marginBottom: 20,
    },
});
