import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Animated } from 'react-native';
import { useRef } from 'react';

const AnswerCard = ({ question, options, correct_option }) => {
  const [showOptions, setShowOptions] = useState(true);
  const [selectedOption, setSelectedOption] = useState(null);

  const cardMove = () => {
    const animatedValue = useRef(new Animated.Value(0)).current;

    Animated.timing(animatedValue, {
      toValue: 4000,
      duration: 4000,
      useNativeDriver: false,
    }).start();
  };

  const checkAnswer = () => {
    if (selectedOption === correct_option) {
      return <Text style={styles.correctAnswer}>Correct!</Text>;
    } else {
      return <Text style={styles.wrongAnswer}>Wrong! Correct answer is {correct_option}</Text>;
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.question}>{question}</Text>
      <Animated.View style={styles.card}>
        <TouchableOpacity onPress={() => { setShowOptions(!showOptions); cardMove(); }}>
          {showOptions && (
            <View style={styles.options}>
              {options.map((option, index) => (
                <TouchableOpacity
                  key={index}
                  style={styles.option}
                  onPress={() => setSelectedOption(option)}
                >
                  <Text style={styles.optionText}>{option}</Text>
                </TouchableOpacity>
              ))}
              {selectedOption && checkAnswer()}
            </View>
          )}
        </TouchableOpacity>
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    marginBottom: 20,
  },
  card: {
    backgroundColor: '#fff',
    borderRadius: 10,
    height: 214, // Fixed height
    width: '100%', // Fill width
  },
  question: {
    fontSize: 18,
    fontWeight: 'normal',
    marginBottom: 10,
    paddingHorizontal: 20,
  },
  options: {
    marginTop: 10,
  },
  option: {
    paddingVertical: 20,
    paddingHorizontal: 20,
    backgroundColor: '#f4f4f4',
    borderRadius: 5,
    marginBottom: 10,
    borderStyle: "solid",
    borderWidth: 1,
    borderColor: '#808080',
  },
  optionText: {
    fontSize: 16,
    textAlign: 'center',
  },
  correctAnswer: {
    color: 'green',
    marginTop: 10,
    paddingHorizontal: 20,
  },
  wrongAnswer: {
    color: 'red',
    marginTop: 10,
    paddingHorizontal: 20,
  },
});

export default AnswerCard;
