import React, { useState, useRef, useEffect } from "react";
import { Text, View, StyleSheet, TouchableOpacity, Animated  } from "react-native";
import data from "../helpers/data";
import Button from '../components/Button';
import * as Progress from 'react-native-progress';
import { useTransition } from 'react-spring';

const Quiz = ({navigation}) => {
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [isCorrect, setIsCorrect] = useState(null);
    const [selectedOption, setSelectedOption] = useState(null);
    const [quizProgress, setQuizProgress] = useState(data.length);
    const translateY = useRef(new Animated.Value(0)).current;
    const fadeAnim = useRef(new Animated.Value(1)).current;

    const transitions = useTransition(selectedOption, {
  from: { opacity: 0, transform: 'translate3d(-25%, 0px, 0px)' },
  enter: { opacity: 1, transform: 'translate3d(0%, 0px, 0px)' },
  leave: { opacity: 0, transform: 'translate3d(25%, 0px, 0px)' },
});

    useEffect(() => {
        if (selectedOption !== null) {
            fadeOut();
        }
    }, [selectedOption]);

    const progress = (currentQuestionIndex + 1) / quizProgress;

    const handleNext = () => {
        if(currentQuestionIndex === data.length -1 ){
            navigation.navigate("QuizEndingScreen");
        } else {
            setCurrentQuestionIndex(currentQuestionIndex + 1);
            setSelectedOption(null);
            setIsCorrect(null);
            fadeAnim.setValue(1); 
            translateY.setValue(0); 
        }
    };

    const fadeOut = () => {
        Animated.parallel([
            Animated.timing(fadeAnim, {
                toValue: 0,
                duration: 200,
                useNativeDriver: true,
            })
        ]).start();
    };

    const moveCardUp = () => {
        Animated.parallel([
            Animated.timing(translateY, {
                toValue: -300, 
                duration: 600,
                useNativeDriver: true,
            }),
        ]).start();
    };

    const handlePressedOption = (pressedOption) => {
        const isAnswerCorrect = data[currentQuestionIndex].correct_option === pressedOption;
        setIsCorrect(isAnswerCorrect);
        setSelectedOption(pressedOption);
        moveCardUp(translateY); 
    };

    return(
        <View style={styles.container}>
            <View style={styles.progressBarContainer}>
                <Progress.Bar progress={progress} width={350} color= 'black' unfilledColor='rgba(217, 217, 217, 1)' borderWidth={0} height={8}  />
            </View> 
            <TouchableOpacity onPress={() => setSelectedOption(data[currentQuestionIndex].options[0])}>
  {transitions.map(({ item, key, props }) =>
    item === data[currentQuestionIndex].options[0] ? (
      <Animated.View key={key} style={props}>
        <View style={[styles.AnswerBox, styles.correctBox]}>
          <Text style={styles.answerText}>{item}</Text>
        </View>
      </Animated.View>
    ) : null,
  )}
</TouchableOpacity>
<TouchableOpacity onPress={() => setSelectedOption(data[currentQuestionIndex].options[1])}>
  {transitions.map(({ item, key, props }) =>
    item === data[currentQuestionIndex].options[1] ? (
      <Animated.View key={key} style={props}>
        <View style={[styles.AnswerBox, styles.correctBox]}>
          <Text style={styles.answerText}>{item}</Text>
        </View>
      </Animated.View>
    ) : null,
  )}
</TouchableOpacity>
<TouchableOpacity onPress={() => setSelectedOption(null)}>
  {transitions.map(({ item, key, props }) =>
    item === null ? (
      <Animated.View key={key} style={props}>
        <View style={[styles.AnswerBox, styles.secondAnswerBox]}>
          <Text style={styles.answerText}>{data[currentQuestionIndex].options[1]}</Text>
        </View>
      </Animated.View>
    ) : null,
  )}
  <Animated.View key={`null-${data[currentQuestionIndex].options[1]}`} style={props}>
    <View style={[styles.AnswerBox, styles.secondAnswerBox]}>
      <Text style={styles.answerText}>{data[currentQuestionIndex].options[0]}</Text>
    </View>
  </Animated.View>

</TouchableOpacity>

            <Button
                color="black"
                mode="contained"
                onPress={handleNext}
            >
                Continue
            </Button>
        </View>
    );
}

export default Quiz;

const styles = StyleSheet.create({
    AnswerBox: {
        width: 361,
        height: 214,
        borderWidth: 3,
        borderRadius: 6,
        borderColor: 'rgba(0, 0, 0, 0.05)',
        backgroundColor: 'rgba(0, 0, 0, 0.05)', 
        alignItems: 'center',
        justifyContent: 'center',
        marginBottom: 10, 
    },
    secondAnswerBox: {
        marginBottom: 0, 
    },
    answerText: {
        textAlign: 'center',
        fontSize: 24,
    },
    question: {
        marginBottom: 10,
        fontSize: 18, 
    },
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    correctBox: {
        borderColor: 'rgba(19, 212, 2, 1)', 
    },
    wrongBox: {
        borderColor: 'rgba(241, 58, 0, 1)', 
    },
    progressBarContainer: {
        width: '100%',
        height: 20,
        marginBottom: 30,
    },
});
