import { Text, View, StyleSheet } from "react-native";
import React, {useState} from "react";
import data from "../helpers/data";

const Quiz = () => {
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    return(
        <View style={styles.container}>
            <Text style={styles.question}>{data[currentQuestionIndex].question}</Text>
            <View style={styles.AnswerBox}>
                <Text style={styles.answerText}>{data[currentQuestionIndex].options[0]}</Text>
            </View>
            <View style={[styles.AnswerBox, styles.secondAnswerBox]}>
                <Text style={styles.answerText}>{data[currentQuestionIndex].options[1]}</Text>
            </View>
        </View>
    );
};

export default Quiz;

const styles = StyleSheet.create({
    AnswerBox: {
        width: 361,
        height: 214,
        borderWidth: 1,
        borderRadius: 6,
        borderColor: 'rgba(0, 0, 0, 0.05)',
        backgroundColor: 'rgba(0, 0, 0, 0.05)', 
        alignItems: 'center',
        justifyContent: 'center',
        marginBottom: 10, // Add marginBottom to create space between the boxes
    },
    secondAnswerBox: {
        marginBottom: 0, // Set marginBottom to 0 for the second box to eliminate extra space
    },
    answerText: {
        textAlign: 'center',
    },
    question: {
        marginBottom: 10, // Add marginBottom to create space between the question and the first box
    },
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
});
