import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet } from 'react-native';

const QuizCard = ({ question, options, correctAnswer }) => {
  const [showOptions, setShowOptions] = useState(false);
  const [selectedOption, setSelectedOption] = useState(null);

  const checkAnswer = () => {
    if (selectedOption === correctAnswer) {
      return <Text style={styles.correctAnswer}>Correct!</Text>;
    } else {
      return <Text style={styles.wrongAnswer}>Wrong! Correct answer is {correctAnswer}</Text>;
    }
  };

  return (
    <TouchableOpacity onPress={() => setShowOptions(!showOptions)} style={styles.card}>
      <Text style={styles.question}>{question}</Text>
      {showOptions && (
        <View style={styles.options}>
          {options.map((option, index) => (
            <TouchableOpacity
              key={index}
              style={styles.option}
              onPress={() => setSelectedOption(option)}
            >
              <Text>{option}</Text>
            </TouchableOpacity>
          ))}
          {selectedOption && checkAnswer()}
        </View>
      )}
    </TouchableOpacity>
  );
};

const styles = StyleSheet.create({
  card: {
    backgroundColor: '#fff',
    borderRadius: 10,
    padding: 20,
    marginBottom: 20,
  },
  question: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  options: {
    marginTop: 10,
  },
  option: {
    paddingVertical: 10,
    paddingHorizontal: 20,
    backgroundColor: '#f4f4f4',
    borderRadius: 5,
    marginBottom: 10,
  },
  correctAnswer: {
    color: 'green',
    marginTop: 10,
  },
  wrongAnswer: {
    color: 'red',
    marginTop: 10,
  },
});

export default QuizCard;
