import React from 'react';
import { Avatar, Button } from 'react-native-elements';
import ImagePicker from 'react-native-image-picker';

const UserPic = ({ avatarSource, onAvatarChange }) => {
  const pickImage = () => {
    ImagePicker.showImagePicker({ title: 'Select Avatar' }, response => {
      if (response.didCancel) {
        console.log('User cancelled image picker');
      } else if (response.error) {
        console.log('ImagePicker Error: ', response.error);
      } else {
        // Update the avatarSource state with the chosen image
        onAvatarChange({ uri: response.uri });
      }
    });
  };

  return (
    <>
      <Avatar rounded source={avatarSource} size="large" />
      <Button title="Change Avatar" onPress={pickImage} />
    </>
  );
};

export default UserPic;
